<PrimaryFuelCO2Coef name="delivered coal">27.3</PrimaryFuelCO2Coef>,
<PrimaryFuelCO2Coef name="delivered gas">14.2</PrimaryFuelCO2Coef>,
<PrimaryFuelCO2Coef name="gas pipeline">14.2</PrimaryFuelCO2Coef>,
